<script setup lang="ts">
import { CirclePlay, MoveLeft, MoveRight } from 'lucide-vue-next';
import { images, counters } from './school-date'
import { onMounted } from 'vue';

interface Counter {
    target: number;
    duration: number;
    current: number;
    suffix: string;
    label: string;
}
// Counting animation function
const animateCounter = (counter: Counter) => {
    const start = 0;
    const step = (counter.target - start) / (counter.duration / 16); // Approx. 60 FPS

    const update = () => {
        if (counter.current < counter.target) {
            counter.current += step;
            requestAnimationFrame(update);
        } else {
            counter.current = counter.target; // Ensure it ends at the target
        }
    };

    update();
};
onMounted(() => {
    counters.value.forEach(animateCounter);
});
</script>
